using System.Collections.Generic; 
using Gennie.Helpers.English; 
using System;
using System.IO;
using Actemium.Gennie.DataObjects;
namespace Actemium.Gennie.CodeGeneration
  {
    public class OutputFile
    {
      public string FileName
      {
        get { return _fileName; }
        set { _fileName = value; }
      } private string _fileName;
      public string PathName
      {
        get { return _pathName; }
        set { _pathName = value; }
      } private string _pathName;
    }
    public class CodeGen:InterfaceLibrary.I_Template
    {
      public void Run(object d, object k)
      {
        DataObject     dataObject = (DataObject)d;
        KeyWordCollection project = (KeyWordCollection)k;
        OutputFile outputFile = new OutputFile();        outputFile.PathName = @"C:\Users\harm.lamers\Desktop\Test\";        string location = Path.GetTempFileName();
        if (!File.Exists(location))
        {
           using (StreamWriter Output = File.CreateText(location)) { }
        }
        using (StreamWriter Output = File.CreateText(location))
        {

      }
      if ((outputFile != null) && (outputFile.FileName != ""))
      {
        if (! new DirectoryInfo( outputFile.PathName).Exists)
          System.IO.Directory.CreateDirectory(outputFile.PathName);
        string finalFile = Path.Combine( outputFile.PathName, outputFile.FileName);
        FileInfo tempFile = new FileInfo(location);
        tempFile.CopyTo( finalFile, true);
        tempFile.Delete();
      }
    }
  }
}

